mapper.update:
UPDATE buying_activity_defs
updated_at=now(), activity_name = #{activityName}, activity_start_at = #{activityStartAt}, activity_end_at = #{activityEndAt}, order_start_at = #{orderStartAt}, order_end_at = #{orderEndAt}, pay_limit = #{payLimit}, status = #{status}, seller_id = #{sellerId}, seller_name = #{sellerName}, shop_id = #{shopId}, shop_name = #{shopName}, business_id = #{businessId},
WHERE id = #{id}

mapper.select:
select count(1)
from buying_activity_defs

    activity_name like concat('%', #{activityName},'%')
     AND  created_at >= #{startAt}  
     AND  created_at <= #{endAt}  
     AND status =#{status}
     AND seller_id =#{sellerId}
     AND business_id =#{businessId}

select id,

from buying_activity_defs

    activity_name like concat('%', #{activityName},'%')
     AND  created_at >= #{startAt}  
     AND  created_at <= #{endAt}  
     AND status =#{status}
     AND seller_id =#{sellerId}
     AND business_id =#{businessId}

ORDER BY id DESC
limit #{offset},#{limit}
