mapper.insert:
INSERT INTO code_usages ()
VALUES (#{activityId}, #{code}, #{orderId}, #{businessId}, #{buyerId}, #{buyerName}, #{sellerId}, #{sellerName},
#{activityName}, #{activityType}, #{discount}, #{originPrice}, #{price}, #{channelType}, #{usedAt}, #{usedCount}, now(), now());

mapper.update:
UPDATE code_usages
updated_at=now(), code = #{code}, activity_id = #{activityId}, order_id = #{orderId}, business_id = #{businessId}, buyer_id = #{buyerId}, buyer_name = #{buyerName}, seller_id = #{sellerId}, seller_name = #{sellerName}, activity_name = #{activityName}, activity_type = #{activityType}, discount = #{discount}, origin_price = #{originPrice}, price = #{price}, channel_type = #{channelType}, used_at = #{usedAt}, used_count = #{usedCount},
WHERE id = #{id}

mapper.select:
select count(1)
from code_usages
where activity_id =  #{activityId}

select id,

from code_usages
    where activity_id = #{activityId}
limit #{pageNo},#{count}

select id,

from code_usages
where activity_id = #{activityId}

select id,

from code_usages
where order_id = #{orderId}

mapper.update:
UPDATE code_usages
updated_at = now(), order_id = #{newId},
WHERE order_id = #{oldId}
